/* eslint-env browser, qunit */
import { toParts, toModule } from '@ractivejs/utils-component'

QUnit.module('source-builder')

const specs = [
  'reference',
  'reordered',
  'script-only',
  'style-only',
  'template-only',
  'links-only',
  'duplicate-link-href',
  'duplicate-link-name',
  'empty-top-levels',
  'script-with-mustache',
  'style-with-mustache',
  'duplicate-require',
  'non-dependency-requires'
]

specs.forEach((spec, index) => {
  QUnit.test(`${spec} source builder`, assert => {
    const sampleDir = `/base/test/samples/${spec}`
    const sample = fetch(`${sampleDir}/component.ractive.html`).then(r => r.text())
    const expectedParts = fetch(`${sampleDir}/component.parts.json`).then(r => r.json())
    const expectedCode = fetch(`${sampleDir}/component.js`).then(r => r.text())
    const expectedMap = fetch(`${sampleDir}/component.js.map`).then(r => r.json())
    const requests = [sample, expectedParts, expectedCode, expectedMap]

    return Promise.all(requests).then(([sample, expectedParts, expectedCode, expectedMap]) => {
      const source = 'component.ractive.html'
      const file = 'component.js'
      const parts = toParts(sample)
      const actualCJS = toModule({ source, file, parts })

      assert.deepEqual(parts, expectedParts, `${spec} parsed correctly`)
      assert.strictEqual(actualCJS.code, expectedCode, `${spec} toCJS code correctly`)
      assert.deepEqual(actualCJS.map, expectedMap, `${spec} toCJS map correctly`)
    })
  })
})
